#include <bits/stdc++.h>
#include <iostream>
#include <fstream>
#include <string>

using namespace std;


#define INPUTFILE "allSubstringsOfLength150.txt"
#define OUTPUTFILE "YesOrNo150.dat"

ifstream fin;
ofstream fout;

string tar = "";
void preKMP(string pattern, int f[])
{
    int m = pattern.length(), k;
    f[0] = -1;
    for (int i = 1; i < m; i++)
    {
        k = f[i - 1];
        while (k >= 0)
        {
            if (pattern[k] == pattern[i - 1])
                break;
            else
                k = f[k];
        }
        f[i] = k + 1;
    }
}
 
//check whether target string contains pattern 
bool KMP(string pattern, string target)
{
    int m = pattern.length();
    int n = target.length();
    int f[m];     
    preKMP(pattern, f);     
    int i = 0;
    int k = 0;        
    while (i < n)
    {
        if (k == -1)
        {
            i++;
            k = 0;
        }
        else if (target[i] == pattern[k])
        {
            i++;
            k++;
            if (k == m)
                return 1;
        }
        else
            k = f[k];
    }
    return 0;
}


void check(){
    fin.open(INPUTFILE);
    fout.open(OUTPUTFILE);

    while(fin)
    {
        string line;
        getline(fin, line);
        if (line != "-1")
        {
            if(KMP(line,tar)){
                fout<<"1"<<endl;
            }
            else{
                fout<<"0"<<endl;
            }
        }
        else
        {
            break;
        }
    }
}




 
int main()
{
    
    check();
    cout<<"Data File for plotting graph created successfully "<<endl;
    return 0;
}


